css:
/* Thai Form 50 ทวิ Styles for Payment Entry */
.print-format {
  font-family: 'Sarabun', Arial, sans-serif;
  font-size: 12px;
  line-height: 1.3;
  color: #000;
}

.form-section {
  margin-bottom: 15px;
  border: 1px solid #000;
  padding: 10px;
}

.section-header {
  background-color: #f0f0f0;
  font-weight: bold;
  padding: 5px;
  margin-bottom: 10px;
}

.field-row {
  margin-bottom: 8px;
}

.signature-box {
  text-align: center;
  border: 1px solid #000;
  padding: 15px;
  min-height: 80px;
}

@media print {
  .print-format {
    font-size: 11px;
  }

  .signature-box {
    min-height: 60px;
  }
}
