css:
/* Thai Withholding Tax Certificate Styles */
@import url('https://fonts.googleapis.com/css2?family=Kanit:wght@300;400;500;600;700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+Thai:wght@300;400;500;600;700&display=swap');

.print-format {
  font-family: 'Kanit', 'Noto Sans Thai', Arial, sans-serif;
  font-size: 12px;
  line-height: 1.4;
  color: #000;
}

/* Header Styles */
.header-box {
  border: 2px solid #000;
  padding: 15px;
  margin-bottom: 10px;
}

.doc-title {
  font-size: 20px;
  font-weight: bold;
  text-align: center;
  margin-bottom: 5px;
}

/* Section Styles */
.section-box {
  border: 1px solid #000;
  padding: 10px;
  margin-bottom: 10px;
}

.section-header {
  font-weight: bold;
  text-decoration: underline;
  margin-bottom: 10px;
}

/* Payer Section */
.payer-section {
  background-color: #f9f9f9;
}

/* Amount Section */
.amount-section {
  background-color: #f0f0f0;
}

.amount-highlight {
  font-weight: bold;
  color: #d00;
  font-size: 14px;
}

/* Signature Section */
.signature-section {
  text-align: center;
  padding: 20px;
}

.signature-line {
  border-bottom: 1px solid #000;
  width: 250px;
  margin: 20px auto;
}

/* Income Type Checkboxes */
.income-checkbox {
  font-size: 11px;
  margin: 5px 0;
}

.income-checkbox.selected {
  font-weight: bold;
}

/* Field Labels */
.field-label {
  font-weight: normal;
  display: inline-block;
  margin-right: 10px;
}

.field-value {
  font-weight: bold;
  display: inline-block;
}

/* Print Specific Styles */
@media print {
  .print-format {
    font-size: 11px;
  }

  .section-box {
    page-break-inside: avoid;
  }

  .header-box {
    border-width: 2px;
  }

  .signature-section {
    page-break-inside: avoid;
  }
}

/* Thai Font Adjustments */
.thai-text {
  font-family: 'Kanit', 'Noto Sans Thai', sans-serif;
  letter-spacing: 0.02em;
}

/* Amount Display */
.amount-display {
  text-align: right;
  font-weight: bold;
  font-size: 13px;
}

/* Tax Rate Display */
.tax-rate {
  color: #d00;
  font-weight: bold;
}

/* Document Number */
.doc-number {
  position: absolute;
  top: 10px;
  right: 20px;
  font-size: 12px;
}
