css:
  * {
        margin: 0;
        padding: 0;
        box-sizing: border-box;
    }

    .print-preview iframe,
    #print-preview iframe {
      height: 1100px !important;
      min-height: 1100px !important;
  }

    @page {
      margin: 0 !important;
      padding: 0 !important;
      size: A4;
    }
      html, body {
        font-family: "Sarabun", sans-serif;
        font-size: 12px;
        line-height: 1.3;
        color: #000;
        background: #fff;
        margin: 5px 5px 0 5px !important;
        padding: 0 !important;
      }

      .form-top {
        display: flex;
        flex-direction: column;
        font-size: 10px;
        margin-bottom: 2mm;
      }

      .form-top-details {
        font-weight: bold;
        margin-right: 2mm;
      }

      /* Whole Form */
      .whole-form {
        border: 1px solid #000;
      }

      /* ========== FORM HEADER ========== */
      .form-header {
        position: relative;
        text-align: center;
        padding: 2px;
        margin-bottom: 10px;
      }

      .cert-number {
        position: absolute;
        top: 5px;
        right: 10px;
        display: flex;
        flex-direction: column;
        justify-content: flex-start;
        align-items: flex-start;
        font-size: 10px;
        gap: 1mm;
      }

      .form-header h1 {
        font-size: 16px;
        font-weight: bold;
        margin: 0 0 3px 0;
      }

      .form-header .subtitle {
        font-size: 12px;
        margin: 0 0 5px 0;
      }

      .form-copies {
        margin-top: 8px;
        display: flex;
        justify-content: center;
        gap: 30px;
        font-size: 12px;
      }

      .form-copies label {
        display: flex;
        align-items: center;
        margin: 0;
      }

      .form-copies input[type="checkbox"] {
        width: 12px;
        height: 12px;
        margin-right: 5px;
      }

      /* ========== SECTION HEADERS ========== */
      .wht-issuer {
        border: 1px solid #000;
        border-radius: 10px;
        margin: 0 2mm 1mm;
        padding: 0 2mm;
      }

      .section-title {
        display: flex;
        justify-content: space-between;
        align-items: center;
        font-weight: bold;
        font-size: 12px;
        margin: 5px 0 5px 0;
        padding: 3px 0;
      }

      /* ========== FORM FIELDS ========== */
      .field-row {
        display: flex;
        align-items: flex-start;
        justify-content: center;
      }

      .field-label {
        font-size: 12px;
        margin-right: 5px;
        white-space: nowrap;
        min-width: fit-content;
        text-align: center;
      }

      .field-comment {
        font-size: 10px;
        font-style: italic;
        font-weight: 100;
      }

      .field-input {
        flex: 1;
        border: none;
        border-bottom: 1px dotted #000;
        padding: 0 2px;
        font-family: "Sarabun", sans-serif;
        font-size: 12px;
        background: transparent;
      }

      .field-input:focus {
        outline: none;
        border-bottom: 1px solid #000;
      }

      /* Tax ID boxes */
      .tax-id-boxes {
        display: inline-flex;
        gap: 2px;
        margin-left: 5px;
      }

      .tax-id-box {
        width: 18px;
        height: 20px;
        border: 1px solid #000;
        text-align: center;
        font-size: 12px;
        padding: 1px;
      }

      /* ========== INCOME TABLE ========== */
      .income-table {
        border-collapse: separate;
        border-spacing: 0;
        margin: 0 2mm 1mm;
        font-size: 10px;
        border: 1px solid #000;
        border-radius: 10px;
      }

      .income-table th,
      .income-table td {
        padding: 4px;
        text-align: center;
      }

      .income-table th {
        font-size: 12px;
        border-bottom: 1px solid #000;
        border-right: 1px solid #000;
      }

      .print-format th {
        color:#000;
        font-weight: normal;
        border-bottom-width: 1px !important;
    }

    .print-format td, .print-format th {
    padding: 2px !important;
}

.checkbox-item input[type="checkbox"] {
    width: 11px !important;
    height: 11px;
}

      .income-table td {
        border-bottom: 1px dotted #000;
        border-right: 1px solid #000;
      }

      .income-table td:last-child {
        border-right: none;
      }

      .income-table .total-row-word {
        height: 30px;
      }

      .income-table #total__amount {
        text-align: right;
      }

      .income-table #total__word {
        border-bottom: none;
        border-right: none;
        font-weight: bold;
        font-size: 10px;
      }

      .income-table #total__word__right {
        border-bottom: none;
        border-left: none;
        text-align: left;
        align-items: center;
      }

      .income-table th:last-child {
        border-right: none;
      }

      .income-table td.text-left {
        text-align: left;
        padding-left: 8px;
      }

      .income-table input {
        width: 100%;
        border: none;
        text-align: center;
        font-size: 10px;
        padding: 0;
        background: transparent;
      }

      .income-table .total-row {
        font-weight: bold;
        padding: 10mm 0;
      }

      .more-whitespace {
        margin-left: 12mm;
      }

      /* ========== CHECKBOX GROUPS ========== */
      .checkbox-row {
        display: flex;
        flex-wrap: wrap;
        gap: 15px;
        margin: 5px 0;
        font-size: 12px;
      }

      .checkbox-item {
        display: flex;
        align-items: center;
      }

      .checkbox-item input[type="checkbox"] {
        width: 11px;
        height: 11px;
        margin-right: 4px;
      }

      .checkbox-item label {
        margin: 0;
      }

      /* ========== DIVIDEND SECTION ========== */
      .dividend-section {
        border: 1px solid #000;
        padding: 8px;
        margin: 10px 0;
        font-size: 12px;
      }

      .dividend-section .title {
        font-weight: bold;
        margin-bottom: 5px;
      }

      .dividend-option {
        margin-left: 20px;
        margin-bottom: 3px;
      }

      /* ========== WARNING BOX ========== */
      .warning-box {
        display: flex;
        justify-content: space-between;
        border: 1px solid #000;
        margin: 0 1mm 1mm 2mm;
        padding: 2mm;
        border-radius: 10px;
        font-size: 12px;
      }

      .warning-box strong {
        font-weight: bold;
        margin-right: 1mm;
      }

      /* ========== SIGNATURE SECTION ========== */
      .signature-section {
        flex: 1;
        border: 1px solid #000;
        margin: 0 2mm 1mm 0;
        padding: 2mm;
        border-radius: 10px;
        font-size: 12px;
      }

      .signature-row {
        display: flex;
        justify-content: space-between;
      }

      .signature-left {
        flex: none;
      }

      .signature-right {
        flex: 1;
        text-align: center;
      }

      .signature-line {
        border-bottom: 1px dotted #000;
        margin: 20px 0 5px 0;
        min-height: 20px;
      }

      .stamp-box {
        border: 1px solid #000;
        height: 50px;
        margin: 10px auto;
        width: 50px;
        display: flex;
        align-items: center;
        justify-content: center;
        font-size: 10px;
        color: #999;
        border-radius: 50%;
      }

      .date-boxes {
        display: flex;
        gap: 10px;
        justify-content: center;
        margin: 10px 0;
      }

      .date-box {
        display: flex;
        align-items: center;
        font-size: 12px;
      }

      .date-box input {
        width: 50px;
        border: none;
        border-bottom: 1px dotted #000;
        text-align: center;
        margin: 0 3px;
        font-size: 12px;
      }

      /* ========== FOOTER NOTE ========== */
      .footer-note {
        display: flex;
        justify-content: flex-start;
        gap: 6mm;
        margin: 0;
        font-size: 10px;
      }

      .footer-note strong {
        font-weight: bold;
      }

      /* ========== UTILITY CLASSES ========== */
      .text-center {
        text-align: center;
      }
      .text-right {
        text-align: right;
      }
      .font-bold {
        font-weight: bold;
      }
      .mb-5 {
        margin-bottom: 5px;
      }
      .mb-10 {
        margin-bottom: 10px;
      }
      .mt-10 {
        margin-top: 10px;
      }
      .w-100 {
        width: 100%;
      }

      /* Attached order */
      .attached-order {
        display: flex;
      }

      .attached-order-left {
        display: flex;
        justify-content: flex-start;
        align-items: flex-start;
        flex-direction: column;
      }

      .attached-order-right {
        flex-grow: 2;
        margin-left: 5px;
        display: grid;
        grid-template-columns: 1fr 1fr 1fr 1fr;
        font-weight: bold;
      }

      .attached-order-right .special-tax {
        font-weight: 100;
      }

      #fund__contributions {
        display: flex;
        justify-content: space-between;
        border: 1px solid #000;
        margin: 0 2mm 1mm;
        padding: 2mm;
        border-radius: 10px;
        font-size: 10px;
      }

      .pay-method {
        display: flex;
        justify-content: space-between;
        border: 1px solid #000;
        margin: 0 2mm 1mm;
        padding: 2mm;
        border-radius: 10px;
        font-size: 12px;
      }

      .warning-wrapper {
        display: flex;
        justify-content: space-between;
      }
