# Helper functions for Thai account management
def get_account_definition(account_key):
    """Get detailed definition for an account"""
    return THAI_ACCOUNT_DEFINITIONS.get(account_key, "No definition available")

def search_thai_accounts(search_term):
    """Search accounts by Thai name or key"""
    search_term = search_term.lower()
    results = {}

    for key, name in THAI_ACCOUNT_GLOSSARY.items():
        if search_term in key.lower() or search_term in name.lower():
            results[key] = name

    return results

def get_accounts_by_category(category_keyword):
    """Get all accounts containing a specific category keyword"""
    category_keyword = category_keyword.lower()
    results = {}

    for key, name in THAI_ACCOUNT_GLOSSARY.items():
        if category_keyword in key.lower() or category_keyword in name.lower():
            results[key] = name

    return results

